import React, { useState, useEffect } from 'react';
import logo from './bonfire.gif';
import iconImg from './icon.png';
import fireImg from './fire.png'
import './App.css';
import "leaflet/dist/leaflet.css"
import {MapContainer, TileLayer, Marker, useMapEvents, Popup } from "react-leaflet";
import { Icon } from 'leaflet';


function App() {
  
  const [markers, setMarkers] = useState([
    {
      geocode: [49.2663341114074, -123.13945137590709],
      popup: "hi"
    },
    {
      geocode: [49.3, -123.13945137590709],
      popup: "hello"
    }
  ]);

  const locIcon = new Icon({
    iconUrl:  iconImg,
    iconSize:[30,30]

  })

  const fireIcon = new Icon({
    iconUrl:  fireImg,
    iconSize:[30,30]

  })


  const [fireMarkers, setFireMarkers] = useState([])

  const [currentTime, setCurrentTime] = useState(0);

 useEffect(() => {
    fetch('/time').then(res => res.json()).then(data => {
      setCurrentTime(data.time);
    })
  }, []);

  const [aboutUsData, setAboutUsData] = useState({
    names: "",
    about: "",
  });
  
 useEffect(() => {
        fetch('/teaminfo').then((res) =>
            res.json().then((data) => {
                setAboutUsData({
                    names: data.names,
                    about: data.about,
                });
            })
        );
    }, []);
  
  const [chart, setChart] = useState({
    current: "",
    historical: "",
    message: "",
  });
  
  const AddMarkerToClick = () => {
    const map = useMapEvents({
      dblclick(event) {
        const { lat, lng } = event.latlng;
        fetch('/testpost', {
         method: 'POST',
         headers: {
          'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            latitude: lat,
            longitude: lng
          })
         
      })
      .then(response => response.json())
      .then(data => 
        fetch(`https://nominatim.openstreetmap.org/reverse?lat=${lat.toString()}&lon=${lng.toString()}&format=json`)
        .then(response => response.json())
        .then(locdata => setMarkers(prevMarkers => [...prevMarkers, { geocode: [lat, lng], popup: locdata.display_name, firedata: getFirePoints(data)}])));
      }
    });
    return null;
  };
  const deleteMarker = (index) => {
    setMarkers(prevMarkers => prevMarkers.filter((_,i) => i !== index));
  };

  function getFirePoints(firedata) {
    console.log(firedata);
    const fires = JSON.parse(firedata['message'])
    const fireCoords =[];
     if (Array.isArray(fires)) {
      fires.forEach(fire => {
          const { LATITUDE, LONGITUDE } = fire;
          //console.log(`Latitude: ${LATITUDE}, Longitude: ${LONGITUDE}`);
          //setFireMarkers(prevMarkers => [...prevMarkers, { geocode: [LATITUDE, LONGITUDE], popup: "New Fire" }]);
          fireCoords.push({geocode: [LATITUDE, LONGITUDE], popup: "New Fire"})
        });
      } else {
        console.log("Invalid format for fire data.");
  }
    //console.log(fireCoords.length);
    return fireCoords;

  }

  useEffect(() => {
    fetch('/comparechart/data')
      .then(res => res.json())
      .then(data => {
        setChart({
          current: data.numcurrent,
          historical: data.numhistorical,
          message: data.message,
        });
      })
  }, []);

  const [figure, setFigure] = useState(0);

  useEffect(() => {
    fetch('/comparechart/figure')
      .then(res => res.blob())
      .then(blob => {
        setFigure(URL.createObjectURL(blob));
      })
  }, []);


  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>The current time is {currentTime}.</p>
        <h1>Team info:</h1>
        <p>{aboutUsData.names}</p>
        <p>{aboutUsData.about}</p>
        <div>
          {figure && <img src={figure}/>}
          <p>{chart.current}</p>
          <p>{chart.historical}</p>
          <p>{chart.message}</p>
        </div>
        <MapContainer center= {[49.2608724,-123.113952]} zoom ={13}>
          <TileLayer
            attribution = '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url = "https://tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <AddMarkerToClick />
          {markers.map((marker, index) => (
            <Marker key={index} position={marker.geocode} icon={locIcon}>
              <Popup>
                <p>{marker.popup}</p>
                <button onClick={() => deleteMarker(index)}>Delete Pin</button>
              </Popup>
            </Marker>
          ))}
          {markers.map((marker, index) => (
            marker.firedata ? marker.firedata.map((fireMarker, fireIndex) => (
            <Marker key={index} position={fireMarker.geocode} icon={fireIcon}>
              <Popup>
                <p>
                  sample text <br /> fire info will go here.
                </p>
              </Popup>
            </Marker>
            )) : []
          ))}
        </MapContainer>
      </header>
    </div>
  );
}

export default App;
