import React, { useState, useEffect } from 'react';
import logo from './bonfire.gif';
import iconImg from './icon.png';
import './App.css';
import "leaflet/dist/leaflet.css"
import {MapContainer, TileLayer, Marker} from "react-leaflet";
import { Icon } from 'leaflet';


function App() {
  
  //placeholder markers
  const markers = [
    {
      geocode: [49.2663341114074, -123.13945137590709],
      popup: "hi"
    },
    {
      geocode: [49.3, -123.13945137590709],
      popup: "hello"
    }
  ];

  const locIcon = new Icon({
    iconUrl:  iconImg,
    iconSize:[30,30]

  })

  const [currentTime, setCurrentTime] = useState(0);

 useEffect(() => {
    fetch('/time').then(res => res.json()).then(data => {
      setCurrentTime(data.time);
    })
  }, []);

  const [aboutUsData, setAboutUsData] = useState({
    names: "",
    about: "",
  });
  
 useEffect(() => {
        fetch('/teaminfo').then((res) =>
            res.json().then((data) => {
                setAboutUsData({
                    names: data.names,
                    about: data.about,
                });
            })
        );
    }, []);
  
  const [chart, setChart] = useState({
    current: "",
    historical: "",
    message: "",
  });
  
  const AddMarkerToClick = () => {
    const map = useMapEvents({
      dblclick(event) {
        const { lat, lng } = event.latlng;
        console.log(`Latitude: ${lat}, Longitude: ${lng}`);
        setMarkers([...markers, { geocode: [lat, lng], popup: "New Marker" }]);
      }
    });
    return null;
  };




  useEffect(() => {
    fetch('/comparechart/data')
      .then(res => res.json())
      .then(data => {
        setChart({
          current: data.numcurrent,
          historical: data.numhistorical,
          message: data.message,
        });
      })
  }, []);

  const [figure, setFigure] = useState(0);

  useEffect(() => {
    fetch('/comparechart/figure')
      .then(res => res.blob())
      .then(blob => {
        setFigure(URL.createObjectURL(blob));
      })
  }, []);


  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>The current time is {currentTime}.</p>
        <h1>Team info:</h1>
        <p>{aboutUsData.names}</p>
        <p>{aboutUsData.about}</p>
        <div>
          {figure && <img src={figure}/>}
          <p>{chart.current}</p>
          <p>{chart.historical}</p>
          <p>{chart.message}</p>
        </div>
        <MapContainer center= {[49.2608724,-123.113952]} zoom ={13}>
          <TileLayer
            attribution = '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url = "https://tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <AddMarkerToClick />
          {markers.map((marker, index) => (
            <Marker key={index} position={marker.geocode} icon={locIcon}>
              {/* You can add a Popup component here if you want */}
            </Marker>
          ))}
        </MapContainer>
      </header>
    </div>
  );
}

export default App;
