import React, { useState, useEffect } from 'react';
import logo from './bonfire.gif';
import './App.css';
import {MapContainer, TileLayer} from "react-leaflet";


function App() {

  const [viewport, setViewport] = useState({
    latitude: 49.2608724,
    longitude: -123.113952,
    zoom: 10,
    width: '100vw',
    height: '100vh'
  })

  const [currentTime, setCurrentTime] = useState(0);

 useEffect(() => {
    fetch('/time').then(res => res.json()).then(data => {
      setCurrentTime(data.time);
    })
  }, []);

  const [aboutUsData, setAboutUsData] = useState({
    names: "",
    about: "",
  });
  
 useEffect(() => {
        fetch('/teaminfo').then((res) =>
            res.json().then((data) => {
                setAboutUsData({
                    names: data.names,
                    about: data.about,
                });
            })
        );
    }, []);
  
  const [chart, setChart] = useState({
    current: "",
    historical: "",
    message: "",
  });

  useEffect(() => {
    fetch('/comparechart/data')
      .then(res => res.json())
      .then(data => {
        setChart({
          current: data.numcurrent,
          historical: data.numhistorical,
          message: data.message,
        });
      })
  }, []);

  const [figure, setFigure] = useState(0);

  useEffect(() => {
    fetch('/comparechart/figure')
      .then(res => res.blob())
      .then(blob => {
        setFigure(URL.createObjectURL(blob));
      })
  }, []);


  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>The current time is {currentTime}.</p>
        <h1>Team info:</h1>
        <p>{aboutUsData.names}</p>
        <p>{aboutUsData.about}</p>
        <div>
          {figure && <img src={figure}/>}
          <p>{chart.current}</p>
          <p>{chart.historical}</p>
          <p>{chart.message}</p>
        </div>
        <MapContainer center= {[49.2608724,-123.113952]} zoom ={13}>
          <TileLayer
            attribution = '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url = "https://tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
        </MapContainer>

      </header>
    </div>
  );
}

export default App;
