import React, { useState, useEffect } from 'react';
import logo from './bonfire.gif';
import './App.css';

function App() {

  const [currentTime, setCurrentTime] = useState(0);

 useEffect(() => {
    fetch('/time').then(res => res.json()).then(data => {
      setCurrentTime(data.time);
    })
  }, []);

  const [aboutUsData, setAboutUsData] = useState({
    names: "",
    about: "",
  });
  
 useEffect(() => {
        fetch('/teaminfo').then((res) =>
            res.json().then((data) => {
                setAboutUsData({
                    names: data.names,
                    about: data.about,
                });
            })
        );
    }, []);
  
  const [chart, setChart] = useState({
    current: "",
    historical: "",
    message: "",
  });

  useEffect(() => {
    fetch('/comparechart/data')
      .then(res => res.json())
      .then(data => {
        setChart({
          current: data.numcurrent,
          historical: data.numhistorical,
          message: data.message,
        });
      })
  }, []);

  const [figure, setFigure] = useState(0);

  useEffect(() => {
    fetch('/comparechart/figure')
      .then(res => res.blob())
      .then(blob => {
        setFigure(URL.createObjectURL(blob));
      })
  }, []);


  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>The current time is {currentTime}.</p>
        <h1>Team info:</h1>
        <p>{aboutUsData.names}</p>
        <p>{aboutUsData.about}</p>
        <div>
          {figure && <img src={figure}/>}
          <p>{chart.current}</p>
          <p>{chart.historical}</p>
          <p>{chart.message}</p>
        </div>
      </header>
    </div>
  );
}

export default App;
